import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from "axios";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    NavLink
} from "react-router-dom";





const User = () => {
    const [per, setPer] = useState([])
    const [films, setFilms] = useState()
    const [species, setSpecies] = useState()
    const [starships,setStarships]=useState()
    const [vehicles,setVehicles]=useState()



    const { id } = useParams();
    // console.log(id);
    // console.log(id+"esto viene por parametro");



    useEffect(() => {
        getDatos()
    }, [])


    const getDatos = async () => {



        try {

            const req = await axios.get(`https://swapi.dev/api/people/${id}`);
            const reqs = req.data;
            setPer(reqs)

        } catch (err) {
            console.log(err);
        }
        setTimeout(async() => {

            const f = await per.films.map((item,) => (
                item
            ))

            const fi = await (f.join('  -  '));
            setFilms(fi)
        }, 1000);
        setTimeout(async() => {

            const s = await per.species.map((item,) => (
                item
            ))
            const Si = await (s.join('  -  '));
            setSpecies(Si)
        }, 1000);


            const n = await per.starships.map((item,) => (
                item
            ))
            const Ni = await (n.join('  -  '));
            setStarships(Ni)
            const v = await per.vehicles.map((item,) => (
                item
            ))
            const Vi = await (v.join('  -  '));
            setVehicles(Vi)






        console.log(per);
    }
    //  console.log(per);

    return (
        <div className="container">

            <div>
                 Nombre:<br/>
                 {per.name}<br />
           Cumplea√±os:<br/> {per.birth_year}<br />
           Creado:<br/> {per.created}<br />
           Editado:<br/> {per.edited}<br />
           Color de ojos:<br/> {per.eye_color}<br />
           Color de pelo:<br/> {per.hair_color}
           Color de piel:<br/> {per.skin_color}
           <br/>
           Peliculas:<br/>{films} <br/>
             Nave Estelar:<br/> {starships} <br/>
             Vehiculo:<br/> {per.vehicles}
             Especie:<br/> {species}     <br/>
             Genero: {per.gender} <br/> 

            </div>
        </div>
    )
}

export default User
